# Función para obtener los datos de la tabla
def get_table_data(html, filename):
    soup = BeautifulSoup(html, 'html.parser')
    table_title = soup.find('h2', class_='LeaderBoardCard_lbcTitle___WI9J').text.strip()
    rows = soup.find_all('tr', class_='LeaderBoardPlayerCard_lbpcTableRow___Lod5')

    print(f'Título de la tabla: {table_title}')
    print('{:<10} {:<25} {:<5} {:<10}'.format('Position', 'Player Name', 'Team', 'Points'))
    data = []
    for row in rows:
        position = row.find('td', class_='LeaderBoardPlayerCard_lbpcTableCell__SnM1o').text.strip()
        player_name = row.find('a', class_='LeaderBoardPlayerCard_lbpcTableLink__MDNgL').text.strip()
        team = row.find('span', class_='LeaderBoardPlayerCard_lbpcTeamAbbr__fGlx3').text.strip()
        points = row.find('td', class_='LeaderBoardWithButtons_lbwbCardValue__5LctQ').find('a').text.strip()

        print('{:<10} {:<25} {:<5} {:<10}'.format(position, player_name, team, points))
        data.append([position, player_name, team, points])

    # Guardar los datos en un archivo CSV
    with open(filename, 'w', newline='') as csvfile:
        csvwriter = csv.writer(csvfile)
        csvwriter.writerow(['Position', 'Player Name', 'Team', 'Points'])
        csvwriter.writerows(data)

